import { Stack, TextField } from "@mui/material";
import React from "react";
import { useLoginString } from "../contexts/TextProvider.jsx";
import { boxLoginSyle } from "../theme.jsx";

//TODO QUITAR TEMAS
const LoginFragment = () => {
  const [labels, assets] = useLoginString();

  const [loginSuccess, setLoginSuccess] = React.useState(false);
  const [loginFail, setLoginFail] = React.useState(false);

  const [formattedValue, setFormattedValue] = useState('');

  const handleInputChange = (event) => {
    const inputValue = event.target.value;

    // Eliminar caracteres no deseados (dejar solo números y '-')
    const sanitizedValue = inputValue.replace(/[^0-9-]/g, '');

    // Formatear según tu criterio: XX-XXXXXXXX-X
    const formatted = sanitizedValue
      .replace(/^(\d{2})/, '$1-') // Añadir guion después de los primeros dos dígitos
      .replace(/(\d{8})(?=\d)/, '$1-'); // Añadir guion después de los siguientes ocho dígitos

    setFormattedValue(formatted);
  };
  return (
    <>
      {" "}
      <Stack spacing={2}>
        <TextField
          sx={{ width: "25vw" }}
          id="cuil"
          label={labels.textFieldLabels.user}
          required
          disabled={loginSuccess}
          error={loginFail}
          onChange={null}
          variant="standard"
        />
        <TextField
          sx={{ width: "25vw" }}
          id="password"
          label={labels.textFieldLabels.password}
          type="password"
          required
          onChange={null}
          error={loginFail}
          disabled={loginSuccess}
          variant="standard"
        />
      </Stack>
    </>
  );
};

export default LoginFragment;
