import { Box, Container, Grid, Pagination } from "@mui/material";
import React, { useState } from "react";
import { centeringStyles } from "../theme";
import InfoCard from "./InfoCard";

/**
 * This function takes in an array of PDFs and returns a view of 3 PDFs in a row.
 * @param {PDF[]} pdfs - An array of PDFs.
 * @returns {JSX.Element} A view of 3 PDFs in a row.
 */
const PdfTable = ({ pdfs }) => {
  /**
   * This function handles the page change event for the pagination component.
   * @param {React.ChangeEvent} event - The page change event.
   * @param {number} page - The new page number.
   */
  const handlePageChange = (event, page) => {
    setCurrentPage(page);
  };

  /**
   * This function calculates the start and end index of the visible PDFs based on the current page and itemsPerPage.
   */
  const calculateVisiblePDFs = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;

    return pdfs.slice(startIndex, endIndex);
  };

  /**
   * This variable holds the total number of PDFs and the current page number.
   */
  const [totalItems, totalPages, currentPage, itemsPerPage] =
    calculateVisiblePDFs();

  /**
   * This variable holds the visible PDFs based on the current page and itemsPerPage.
   */
  const visiblePdfs = calculateVisiblePDFs();

  return (
    <Container sx={{ height: "80vh", width: "100vw" }}>
      <Grid
        container
        spacing={3}
        width={"100%"}
        sx={{ ...centeringStyles, height: "80vh", width: "90vw" }}
      >
        {visiblePdfs.map((pdf, index) => (
          <Grid item key={index}>
            <Box
              display="flex"
              flexDirection="column"
              justifyContent="center"
              alignItems="center"
              height="100%"
            >
              <InfoCard state={pdf === null} pdf={pdf} />
            </Box>
          </Grid>
        ))}
      </Grid>
      <Box display="flex" justifyContent="center" alignItems="center" mt={3}>
        <Pagination
          count={totalPages}
          page={currentPage}
          onChange={handlePageChange}
          color="primary"
        />
      </Box>
    </Container>
  );
};

export default PdfTable;
