import React, { useState } from "react";
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Typography,
} from "@mui/material";
import { getHSL } from "../../utiles";
import { Handshake } from "@mui/icons-material";

const Calendar = ({ intStart, intEnd, msg }) => {
  const [currentDate, setCurrentDate] = useState(intStart);

  const daysInMonth = getDaysInMonth(currentDate);

  const monthName = new Intl.DateTimeFormat("es", { month: "long" }).format(
    currentDate
  );
  const getTableCel = (day, index) => {
    let { h, s, l } = { h: 228, s: 0, l: 100 };
    let color = getHSL(228, 0, 100);
    if (intStart === intEnd) {
      if (day != null && day === intEnd.getDate()) {
        h = 204;
        s = 70;
        l = 63;
      }
      // estoy en marcado de un dia
    } else {
      //tengo que colorar rango
      if (day != null && day <= intEnd.getDate()) {
        h = 6;
        s = 39;
        l = 81;
      }
    }
    return (
      <TableCell
      onMouseOver={open}
        key={index}
        sx={{
          border: "1px solid #ddd",
          textAlign: "center",
          padding: "2px",
          backgroundColor: getHSL(h, s, l),
          "&:hover": {
            backgroundColor: getHSL(h, s, l - 10),
          },
        }}
      >
        {day || " "}

      </TableCell>
    );
  };

  return (
    <div>
      <Typography variant="h7" paddingBottom={2}>
        {monthName.charAt(0).toUpperCase() +
          monthName.slice(1) +
          " " +
          currentDate.getFullYear()}
      </Typography>
      <TableContainer
        component={Paper}
        sx={{ maxWidth: "300px", margin: "auto" }}
      >
        <Table>
          <TableHead>
            <TableRow>
              {["Dom", "Lun", "Mar", "Mié", "Jue", "Vie", "Sáb"].map(
                (day, index) => (
                  <TableCell
                    key={index}
                    sx={{
                      backgroundColor: "#f2f2f2",
                      textAlign: "center",
                      padding: "2px",
                    }}
                  >
                    {day}
                  </TableCell>
                )
              )}
            </TableRow>
          </TableHead>
          <TableBody>
            {chunkArray(daysInMonth, 7).map((row, rowIndex) => (
              <TableRow key={rowIndex}>
                {row.map((day, index) => getTableCel(day, index))}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

const getDaysInMonth = (date) => {
  const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
  const startingDayOfWeek = firstDay.getDay(); // 0 (Sunday) to 6 (Saturday)
  const daysInMonth = [];

  // Add empty cells for days before the first day of the month
  for (let i = 0; i < startingDayOfWeek; i++) {
    daysInMonth.push(null);
  }

  const lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);

  // Add the days of the month
  for (let i = 1; i <= lastDay.getDate(); i++) {
    daysInMonth.push(i);
  }

  return daysInMonth;
};

const chunkArray = (arr, size) => {
  const chunkedArray = [];
  for (let i = 0; i < arr.length; i += size) {
    chunkedArray.push(arr.slice(i, i + size));
  }
  return chunkedArray;
};

export default Calendar;
