import { Divider, Grid, Paper, Typography, TextField } from "@mui/material";
import React from "react";
import UserBadge from "./UserBadge";
import { centeringStyles } from "../theme.jsx";
import { IconButton } from "@mui/material";
import { GetApp, Visibility } from "@mui/icons-material";
import { Box, Stack } from "@mui/system";
import { shortEmail } from "../utiles";

const iuser = {
  name: "Ignacio",
  cuil: "11-37425457-8",
  lastname: "Romang",
  email: "ignacioromang@outlook.com",
  address: {
    street: "calle falsa",
    city: "ciudad falsa",
    state: "Inunda Fe",
  },
  phone: "+(12) 3214-645123",
};

function generateRandomName() {
  const adjectives = [
    "Red",
    "Blue",
    "Green",
    "Happy",
    "Sunny",
    "Brilliant",
    "Gentle",
    "Clever",
    "Kind",
  ];
  const nouns = [
    "Cat",
    "Dog",
    "Mountain",
    "Ocean",
    "Tree",
    "Sun",
    "Moon",
    "Star",
    "River",
  ];

  const randomAdjective =
    adjectives[Math.floor(Math.random() * adjectives.length)];
  const randomNoun = nouns[Math.floor(Math.random() * nouns.length)];

  return `${randomAdjective} ${randomNoun}`;
}

const randomName = generateRandomName();
const sx = {
  width: "50vw", // Altura por defecto

  // Ajusta la altura para tamaños de pantalla específicos
  "@media (max-width: 600px)": {
    width: "90vw", // Altura para tamaños pequeños de pantalla (xs)
  },

  "@media (min-width: 601px) and (max-width: 960px)": {
    width: "90vw", // Altura para tamaños medianos de pantalla (sm)
  },

  "@media (min-width: 961px) and (max-width: 1280px)": {
    width: "50vw", // Altura para tamaños grandes de pantalla (md)
  },

  "@media (min-width: 1281px) and (max-width: 1920px)": {
    // pantalla 1080
    width: "45vw", // Altura para tamaños extra grandes de pantalla (lg)
  },

  "@media (min-width: 1921px)": {
    width: "35vw", // Altura para tamaños muy grandes de pantalla (xl)
  },
};

const ProfileAEdata = () => {
  const [ae, setAe] = React.useState(false);

  const handleDownload = () => {
    // Lógica para descargar el PDF
    console.log("Descargando el PDF");
  };

  const handleView = () => {
    // Lógica para visualizar el PDF
    console.log("Visualizando el PDF");
  };
  return (
    <Box sx={centeringStyles}>
      <Stack sx={centeringStyles}>
        <UserBadge username={randomName} isActive={ae} />
        <Typography variant="body1" paddingRight={17} fontSize={10}>
          CUIL
        </Typography>
        <Typography variant="h5">{iuser.cuil}</Typography>
      </Stack>
      {ae && (
        <>
          <Divider />
          <Stack direction={"row"} sx={centeringStyles}>
            <Typography variant="body1" fontSize={14}>
              Comprobante
            </Typography>
            <IconButton onClick={handleDownload} aria-label="Descargar PDF">
              <GetApp />
            </IconButton>
            <IconButton onClick={handleView} aria-label="Visualizar PDF">
              <Visibility />
            </IconButton>
            city
          </Stack>
        </>
      )}
    </Box>
  );
};

export default ProfileAEdata;
