import React, { useState } from "react";
import PropTypes from "prop-types";
import {
  Button,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
} from "@mui/material";
import "./Calendar.css";

const Calendar = ({
  startDate,
  endDate,
  intervalStartDate,
  intervalEndDate,
}) => {
  const [currentDate, setCurrentDate] = useState(new Date());

  const nextMonth = () => {
    const nextDate = new Date(currentDate);
    nextDate.setMonth(currentDate.getMonth() + 1);
    setCurrentDate(nextDate);
  };

  const prevMonth = () => {
    const prevDate = new Date(currentDate);
    prevDate.setMonth(currentDate.getMonth() - 1);
    setCurrentDate(prevDate);
  };

  const handleDateClick = (day) => {
    // Handle the click event if needed
  };

  const isInRange = (day) => {
    return (
      day >= intervalStartDate.getDate() && day <= intervalEndDate.getDate()
    );
  };

  const getDaysInMonth = (date) => {
    const firstDay = new Date(date.getFullYear(), date.getMonth(), 1);
    const startingDayOfWeek = firstDay.getDay(); // 0 (Sunday) to 6 (Saturday)
    const daysInMonth = [];

    // Add empty cells for days before the first day of the month
    for (let i = 0; i < startingDayOfWeek; i++) {
      daysInMonth.push(null);
    }

    const lastDay = new Date(date.getFullYear(), date.getMonth() + 1, 0);

    // Add the days of the month
    for (let i = 1; i <= lastDay.getDate(); i++) {
      daysInMonth.push(i);
    }

    return daysInMonth;
  };

  const chunkArray = (arr, size) => {
    const chunkedArray = [];
    for (let i = 0; i < arr.length; i += size) {
      chunkedArray.push(arr.slice(i, i + size));
    }
    return chunkedArray;
  };

  const daysInMonth = getDaysInMonth(currentDate);

  return (
    <div>
      <h2>{`${currentDate.toLocaleString("default", {
        month: "long",
      })} ${currentDate.getFullYear()}`}</h2>
      <Button onClick={prevMonth}>Anterior</Button>
      <Button onClick={nextMonth}>Siguiente</Button>
      <TableContainer
        component={Paper}
        sx={{ maxWidth: "600px", margin: "auto" }}
      >
        <Table>
          <TableHead>
            <TableRow>
              {["Dom", "Lun", "Mar", "Mié", "Jue", "Vie", "Sáb"].map(
                (day, index) => (
                  <TableCell
                    key={index}
                    sx={{
                      backgroundColor: "#f2f2f2",
                      textAlign: "center",
                      padding: "10px",
                    }}
                  >
                    {day}
                  </TableCell>
                )
              )}
            </TableRow>
          </TableHead>
          <TableBody>
            {chunkArray(daysInMonth, 7).map((row, rowIndex) => (
              <TableRow key={rowIndex}>
                {row.map((day, index) => {
                  const isStart = day === startDate.getDate();
                  const isEnd = day === endDate.getDate();
                  const isInInterval = isInRange(day);

                  return (
                    <TableCell
                      key={index}
                      sx={{
                        border: "1px solid #ddd",
                        textAlign: "center",
                        padding: "10px",
                        cursor: "pointer",
                        backgroundColor: isStart
                          ? "#ffcccb"
                          : isEnd
                          ? "#90ee90"
                          : isInInterval
                          ? "#add8e6"
                          : "inherit",
                        "&:hover": {
                          backgroundColor: "#f0f0f0",
                        },
                      }}
                      onClick={() => handleDateClick(day)}
                    >
                      {day || " "}
                    </TableCell>
                  );
                })}
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

Calendar.propTypes = {
  startDate: PropTypes.instanceOf(Date).isRequired,
  endDate: PropTypes.instanceOf(Date).isRequired,
  intervalStartDate: PropTypes.instanceOf(Date).isRequired,
  intervalEndDate: PropTypes.instanceOf(Date).isRequired,
};

export default Calendar;
