import { Paper, Typography } from "@mui/material";
import axios from "axios";
import React from "react";
import { useParams } from "react-router-dom";

const PDFViewer = () => {
  const [pdf, setPdf] = React.useState([]);
  //TODO leer pdf de back end
  const { pdfid } = useParams();
  React.useEffect(() => {
    const fetchData = async () => {
      try {
        let url = process.env.REACT_APP_BACK_URL;
        const response = await axios.post(url + "/api/pdf/getpdf", {
          pdfId: pdfid,
        });
        setPdf(response.data);
      } catch (error) {
        console.error("Error fetching PDF viewer:", error);
        // Handle error if needed
      }
    };

    fetchData();
  }, []);
  return (
    <Card>
      <CardContent>
        <Typography variant="h5" component="div">
          {title}
        </Typography>

        <Typography color="textSecondary" gutterBottom>
          Fecha de Creación: {creationDate}
        </Typography>
        <Typography color="textSecondary">
          Fecha de Edición: {editDate}
        </Typography>

        {/* Contenido de la noticia */}
        <Typography
          variant="body2"
          component="div"
          style={{ marginTop: "10px" }}
        >
          {content}
        </Typography>

        <Button
          variant="contained"
          color="primary"
          onClick={onDownloadPDF}
          style={{ marginTop: "10px" }}
        >
          Descargar PDF
        </Button>
      </CardContent>
    </Card>
  );
};
/**
 *          
    <Paper sx={{ width: "98vw", height: "98vh" }}>
      <Typography paddingTop={2} variant="h5">
        {pdf.title}
      </Typography>
       <iframe
        title="PDF Viewer"
        src={`data:application/pdf;base64,${pdf.content}`}
        width="100%"
        height="100%"
        style={{ border: "none" }}
      ></iframe>
    </Paper>
*/

export default PDFViewer;
