import {
  Button,
  Card,
  CardContent,
  CardMedia,
  Paper,
  Typography,
  createTheme,
} from "@mui/material";
import axios from "axios";
import React from "react";
import { useParams } from "react-router-dom";

const theme = createTheme({
  spacing: 2,
});

const high_res = {
  display: {
    xs: "none",
    md: "flex",
    lg: "flex",
  },
};

const PDFViewer = () => {
  const [pdf, setPdf] = React.useState([]);
  const { pdfid } = useParams();

  React.useEffect(() => {
    const fetchData = async () => {
      try {
        let url = process.env.REACT_APP_BACK_URL;
        const response = await axios.post(url + "/api/pdf/getpdf", {
          pdfId: pdfid,
        });
        setPdf(response.data);
      } catch (error) {
        console.error("Error fetching PDF viewer:", error);
        // Handle error if needed
      }
    };

    fetchData();
  }, []);
  return (
    <>
      <Paper sx={{ width: "100%", height: "100%" }}>
        <Typography variant="h2" component="div">
          {pdf.title}
        </Typography>

        <Typography color="textSecondary" gutterBottom>
          Fecha de Creación: {"ASDASDF"}
        </Typography>
        <Typography color="textSecondary">
          Fecha de Edición: {"ASDFASF"}
        </Typography>
      </Paper>

      <iframe
        title="PDF Viewer"
        src={`data:application/pdf;base64,${pdf.content}`}
        width="100vw"
        height="100vh"
        style={{ border: "none" }}
      ></iframe>
    </>
  );
};
/**
 *          
    <Paper sx={{ width: "98vw", height: "98vh" }}>
      <Typography paddingTop={2} variant="h5">
        {pdf.title}
      </Typography>
       <iframe
        title="PDF Viewer"
        src={`data:application/pdf;base64,${pdf.content}`}
        width="100%"
        height="100%"
        style={{ border: "none" }}
      ></iframe>
    </Paper>
*/

export default PDFViewer;
